mapper.select:
select B.* from
(select rownum R , PRODUCT_ASK.* from PRODUCT_ASK order by PA_IDX desc) B
where R between #{param1} and #{param2}

select B.* from
(select rownum R , PRODUCT_ASK.* from PRODUCT_ASK order by PA_IDX desc) B
where PA_P_IDX = #{param3} AND R between #{param1} and #{param2}
