mapper.update:
update PRODUCT set P_NAME=#{param2} , P_CODE=#{param3} , P_FILENAME1=#{param4} ,
P_FILEPATH=#{param5},P_CATEGORY=#{param6},P_PRICE=#{param7}, 
P_STOCK=#{param8},P_FILENAME2=#{param9},P_FILENAME3=#{param10}
where P_IDX = #{param1}

mapper.select:
select A.* from
	(SELECT rownum R, PRODUCT.* from product where P_CATEGORY = #{param3} order by P_IDX desc) A
where R between #{param1} and #{param2}
